#include <stdio.h>
#include <string.h>

#define N 300000
#define MOD 1000000007

int n;
char s[N + 1];
int dp[N + 1][2][2];

int main() {
    scanf("%s", s);
    n = strlen(s);
    dp[n][0][0] = 1;
    for (int i = n - 1; i >= 0; i--) {
        for (int j = 0; j < 2; j++) {
            for (int k = 0; k < 2; k++) {
                dp[i][j][k] = 0;
                if (s[i] != '?') {
                    if (s[i] == '1') {
                        dp[i][j][k] = dp[i + 1][j][k];
                    } else if (j == 0) {
                        dp[i][1][k] = (dp[i][1][k] + dp[i + 1][0][k]) % MOD;
                    } else {
                        dp[i][0][k] = (dp[i][0][k] + dp[i + 1][1][k]) % MOD;
                    }
                } else {
                    if (j == 0) {
                        dp[i][1][k] = (dp[i][1][k] + dp[i + 1][0][k]) % MOD;
                        dp[i][1][1] = (dp[i][1][1] + dp[i + 1][0][1]) % MOD;
                    } else {
                        dp[i][0][k] = (dp[i][0][k] + dp[i + 1][1][k]) % MOD;
                        dp[i][0][0] = (dp[i][0][0] + dp[i + 1][1][0]) % MOD;
                    }
                }
            }
        }
    }
    int ans = 0;
    for (int i = 0; i < 2; i++) {
        for (int j = 0; j < 2; j++) {
            ans = (ans + dp[0][i][j]) % MOD;
        }
    }
    printf("%d\n", ans);
    return 0;
}
