#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

using namespace std;

const int N = 300005;
const int mod = 1e9 + 7;

int n;
int dp[N][2][2];
char s[N];
int pre[N];

int main() {
    cin >> s + 1;
    n = strlen(s + 1);
    for (int i = 1; i <= n; i++) {
        pre[i] = pre[i - 1] + (s[i] == '?' ? 1 : 0);
    }
    dp[1][0][0] = 1;
    for (int i = 1; i <= n; i++) {
        for (int j = 0; j < 2; j++) {
            for (int k = 0; k < 2; k++) {
                if (i == n) {
                    if (j == 0 && k == 0) {
                        cout << (dp[i][j][k] + mod - 1) % mod << endl;
                        return 0;
                    }
                } else {
                    int l = pre[i + 2] - pre[i];
                    if (s[i] != '?' && s[i] != '1' + j) continue;
                    if (l == 0) {
                        dp[i + 1][j][k] = (dp[i + 1][j][k] + dp[i][j][k]) % mod;
                    } else if (l == 1) {
                        if (s[i + 1] != '?' && s[i + 1] != '1' + k) continue;
                        dp[i + 1][j][k] = (dp[i + 1][j][k] + dp[i][j][k]) % mod;
                    } else {
                        if (s[i + 1] != '?' && s[i + 1] != '1' + k) continue;
                        if (s[i + 2] != '?' && s[i + 2] != '1' + k) continue;
                        dp[i + 3][j][k] = (dp[i + 3][j][k] + dp[i][j][k]) % mod;
                    }
                }
            }
        }
    }
    return 0;
}
