import java.io.*;
import java.util.*;

public class Main {
    static final int N = 300005;
    static final int mod = (int) 1e9 + 7;
    static int n;
    static char[] s = new char[N];
    static int[][] dp = new int[N][2];
    static int[][] pre = new int[N][2];

    public static void main(String[] args) throws IOException {
        Scanner sc = new Scanner(System.in);
        PrintWriter pw = new PrintWriter(System.out);
        s = sc.next().toCharArray();
        n = s.length;
        dp[0][0] = 1;
        dp[0][1] = 1;
        for (int i = 1; i < n; i++) {
            for (int j = 0; j < 2; j++) {
                dp[i][j] = (dp[i][j] + dp[i - 1][j]) % mod;
                if (s[i] != '?') {
                    dp[i][j] = (dp[i][j] + (s[i] - '0') * dp[i - 1][j]) % mod;
                }
            }
            for (int j = 0; j < 2; j++) {
                if (s[i] == '1') {
                    dp[i][j] = (dp[i][j] + dp[i - 1][1 - j]) % mod;
                } else if (s[i] == '0') {
                    dp[i][j] = (dp[i][j] + dp[i - 1][j]) % mod;
                }
            }
        }
        int ans = (dp[n - 1][0] + dp[n - 1][1]) % mod;
        for (int i = 0; i < n - 1; i++) {
            if (s[i] == '?') {
                if (s[i + 1] == '1') {
                    ans = (ans + dp[i][0]) % mod;
                } else if (s[i + 1] == '0') {
                    ans = (ans + dp[i][1]) % mod;
                }
            }
        }
        pw.println(ans);
        pw.close();
    }
}

