MOD = 10**9 + 7

def solve(s):
    n = len(s)
    dp = [[0] * 2 for _ in range(n + 1)]
    dp[0][0] = 1
    for i in range(1, n + 1):
        if s[i - 1] == '?':
            dp[i][0] = (dp[i - 1][0] + dp[i - 1][1]) % MOD
            dp[i][1] = dp[i - 1][1]
        else:
            dp[i][0] = dp[i - 1][0]
            if s[i - 1] == '1':
                dp[i][1] = (dp[i - 1][0] + dp[i - 1][1]) % MOD
            else:
                dp[i][1] = dp[i - 1][1]
    ans = 0
    for i in range(n - 1, -1, -3):
        if s[i] == '?':
            if s[i + 1] == '?':
                ans = (ans + dp[i + 1][0] * dp[i + 2][0]) % MOD
                ans = (ans + dp[i + 1][1] * dp[i + 2][1]) % MOD
                ans = (ans + dp[i + 1][0] * dp[i + 2][1]) % MOD
                ans = (ans + dp[i + 1][1] * dp[i + 2][0]) % MOD
            elif s[i + 1] == '1':
                ans = (ans + dp[i + 1][0] * dp[i + 2][1]) % MOD
            else:
                ans = (ans + dp[i + 1][1] * dp[i + 2][0]) % MOD
    return ans

