#include <iostream>
#include <vector>
#include <algorithm>
#include <numeric>

using namespace std;

const int MOD = 1e9 + 7;

int main() {
    int N;
    cin >> N;

    vector<int> fac(N + 1);
    fac[0] = 1;
    for (int i = 1; i <= N; i++) {
        fac[i] = (1LL * fac[i - 1] * i) % MOD;
    }

    vector<int> inv(N + 1);
    inv[N] = pow(fac[N], MOD - 2, MOD);
    for (int i = N - 1; i >= 0; i--) {
        inv[i] = (1LL * inv[i + 1] * (i + 1)) % MOD;
    }

    vector<int> dp(N + 1);
    for (int i = 1; i <= N; i++) {
        dp[i] = (1LL * fac[i - 1] * inv[i] % MOD * inv[i - 1] % MOD * (1LL * i * (i - 1) % MOD)) % MOD;
    }

    int ans = 0;
    for (int i = 1; i <= N; i++) {
        ans = (ans + dp[i]) % MOD;
    }

    cout << ans << endl;

    return 0;
}
