import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        int d = scanner.nextInt();
        int x = scanner.nextInt();
        int[] a = new int[n];
        for (int i = 0; i < n; i++) {
            a[i] = scanner.nextInt();
        }

        int[] days = new int[2 * n + 1];
        for (int i = 0; i < n; i++) {
            int day = 1;
            for (int j = 0; j < 100; j++) {
                if (day > d) {
                    break;
                }
                days[day] += 1;
                day = 2 * a[i] + day;
            }
        }

        int ans = 0;
        for (int i = 1; i <= d; i++) {
            if (days[i] == 0) {
                continue;
            }
            int day = i;
            while (day <= d) {
                if (days[day] == 0) {
                    break;
                }
                day += i;
            }
            ans += (day - i) / i;
        }
        ans += x;
        System.out.println(ans);
    }
}
