#include <iostream>
#include <vector>
#include <string>

using namespace std;

void paintGrid(int a, int b) {
    int h = 2 * a + b - 1;
    int w = a + b - 1;

    vector<string> grid(h);
    for (int i = 0; i < h; i++) {
        grid[i].resize(w, '.');
    }

    for (int i = 0; i < a; i++) {
        for (int j = 0; j < w; j++) {
            if (i == j || i == w - 1 - j) {
                grid[i][j] = '#';
            }
        }
    }

    for (int i = 0; i < b; i++) {
        for (int j = 0; j < w; j++) {
            if (i == j || i == w - 1 - j) {
                grid[h - 1 - i][j] = '#';
            }
        }
    }

    for (int i = 0; i < a - 1; i++) {
        for (int j = 0; j < w; j++) {
            if (j == i + 1) {
                grid[i + 1][j] = '.';
            }
        }
    }

    for (int i = 0; i < b - 1; i++) {
        for (int j = 0; j < w; j++) {
            if (j == w - 1 - i - 1) {
                grid[h - 2 - i][j] = '.';
            }
        }
    }

    cout << w << " " << h << endl;
    for (const auto& row : grid) {
        cout << row << endl;
    }
}

int main() {
    int a, b;
    cin >> a >> b;
    paintGrid(a, b);
    return 0;
}
