import java.util.Scanner;

public class Main {
    static int[][] grid = new int[100][100];
    static int[][] directions = {{0, 1}, {0, -1}, {1, 0}, {-1, 0}};
    static int countWhite = 0;
    static int countBlack = 0;

    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int A = scanner.nextInt();
        int B = scanner.nextInt();
        int h = 1;
        int w = 1;
        int x = 1;
        int y = 1;
        int white = 0;
        int black = 0;

        while (white < A || black < B) {
            if (white < A) {
                dfs(x, y, 1);
                white++;
            }
            if (black < B) {
                dfs(x, y, 0);
                black++;
            }
            h++;
            w++;
            x = 1;
            y = 1;
        }

        System.out.println(h + " " + w);
        for (int i = 1; i <= h; i++) {
            for (int j = 1; j <= w; j++) {
                if (grid[i][j] == 1) {
                    System.out.print(".");
                } else {
                    System.out.print("#");
                }
            }
            System.out.println();
        }
    }

    public static void dfs(int x, int y, int color) {
        if (x > 0 && x <= 100 && y > 0 && y <= 100 && grid[x][y] == 0) {
            grid[x][y] = color;
            for (int[] direction : directions) {
                dfs(x + direction[0], y + direction[1], color);
            }
        }
    }
}
