from collections import deque

def paint_grid(A, B):
    h = 2 * A + 2 * B + 1
    w = 2 * A + 2 * B + 1
    grid = [['.' for _ in range(w)] for _ in range(h)]

    def dfs(i, j):
        if 0 <= i < h and 0 <= j < w and grid[i][j] == '.':
            grid[i][j] = '#'
            dfs(i - 1, j)
            dfs(i + 1, j)
            dfs(i, j - 1)
            dfs(i, j + 1)

    for i in range(1, h - 1):
        for j in range(1, w - 1):
            if grid[i][j] == '.':
                dfs(i, j)

    def bfs(i, j):
        queue = deque([(i, j)])
        grid[i][j] = '#'
        while queue:
            x, y = queue.popleft()
            for dx, dy in [(1, 0), (-1, 0), (0, 1), (0, -1)]:
                nx, ny = x + dx, y + dy
                if 0 <= nx < h and 0 <= ny < w and grid[nx][ny] == '.':
                    grid[nx][ny] = '#'
                    queue.append((nx, ny))

    for i in range(1, A):
        bfs(1 + 2 * i, 1 + 2 * i)

    for i in range(1, B):
        bfs(1 + 2 * i, w - 2 * i)

    for i in range(1, h - 1):
        for j in range(1, w - 1):
            if grid[i][j] == '.':
                bfs(i, j)

    print(h - 2, w - 2)
    for row in grid[1:-1]:
        print(''.join(row[1:-1]))

A, B = map(int, input().split())
paint_grid(A, B)
