import java.util.Arrays;
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        int[] a = new int[n + 1];
        for (int i = 1; i <= n; i++) {
            a[i] = scanner.nextInt();
        }
        Arrays.sort(a, 1, n + 1);
        int[] pre = new int[n + 1];
        int[] suf = new int[n + 1];
        pre[1] = Math.abs(a[1]);
        for (int i = 2; i <= n; i++) {
            pre[i] = pre[i - 1] + Math.abs(a[i] - a[i - 1]);
        }
        suf[n] = Math.abs(a[n]);
        for (int i = n - 1; i >= 1; i--) {
            suf[i] = suf[i + 1] + Math.abs(a[i] - a[i + 1]);
        }
        for (int i = 1; i <= n; i++) {
            System.out.println(pre[i - 1] + suf[i + 1]);
        }
    }
}
