#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

const int MAXN = 105;

int n, m, a, b;
vector<vector<int>> grid;
vector<vector<int>> visited;

void dfs(int x, int y) {
    if (x < 0 || x >= n || y < 0 || y >= m || visited[x][y] || grid[x][y] == 1) return;
    visited[x][y] = 1;
    dfs(x - 1, y);
    dfs(x + 1, y);
    dfs(x, y - 1);
    dfs(x, y + 1);
}

void solve() {
    cin >> a >> b;
    n = a + b + 2;
    m = max(a, b) + 1;
    grid.resize(n, vector<int>(m, 0));
    visited.resize(n, vector<int>(m, 0));
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < m; j++) {
            if (i == 0 || i == n - 1 || j == 0) {
                grid[i][j] = 1;
            } else {
                if (i == 1 && j == 1) {
                    grid[i][j] = 1;
                } else if (i == 1 && j == m - 1) {
                    grid[i][j] = 1;
                } else if (i == n - 2 && j == 1) {
                    grid[i][j] = 1;
                } else if (i == n - 2 && j == m - 1) {
                    grid[i][j] = 1;
                } else if (i == 2 && j == 2) {
                    grid[i][j] = 1;
                } else if (i == 2 && j == m - 2) {
                    grid[i][j] = 1;
                } else if (i == n - 3 && j == 2) {
                    grid[i][j] = 1;
                } else if (i == n - 3 && j == m - 2) {
                    grid[i][j] = 1;
                } else {
                    grid[i][j] = 0;
                }
            }
        }
    }
    for (int i = 1; i < n - 1; i++) {
        for (int j = 1; j < m - 1; j++) {
            if (grid[i][j] == 0) {
                int cnt = 0;
                dfs(i, j);
                for (int x = 0; x < n; x++) {
                    for (int y = 0; y < m; y++) {
                        if (visited[x][y]) cnt++;
                    }
                }
                if (cnt == a) {
                    for (int x = 0; x < n; x++) {
                        for (int y = 0; y < m; y++) {
                            visited[x][y] = 0;
                        }
                    }
                    dfs(i, j);
                    for (int x = 0; x < n; x++) {
                        for (int y = 0; y < m; y++) {
                            if (visited[x][y]) grid[x][y] = 1;
                        }
                    }
                    break;
                }
            }
        }
        if (i == n - 2) {
            for (int j = 1; j < m - 1; j++) {
                if (grid[i][j] == 0) {
                    int cnt = 0;
                    dfs(i, j);
                    for (int x = 0; x < n; x++) {
                        for (int y = 0; y < m; y++) {
                            if (visited[x][y]) cnt++;
                        }
                    }
                    if (cnt == a) {
                        for (int x = 0; x < n; x++) {
                            for (int y = 0; y < m; y++) {
                                visited[x][y] = 0;
                            }
                        }
                        dfs(i, j);
                        for (int x = 0; x < n; x++) {
                            for (int y = 0; y < m; y++) {
                                if (visited[x][y]) grid[x][y] = 1;
                            }
                        }
                        break;
                    }
                }
            }
        }
    }
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < m; j++) {
            if (grid[i][j] == 0) {
                grid[i][j] = 2;
            }
        }
    }
    cout << n - 2 << " " << m - 2 << endl;
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < m; j++) {
            if (grid[i][j] == 1) {
                cout << "#";
            } else {
                cout << "..";
            }
        }
        cout << endl;
    }
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);
    int t = 1;
    while (t--) {
        solve();
    }
    return 0;
}
