import java.util.Scanner;

public class Main {
    static int[][] grid = new int[100][100];
    static int[][] visited = new int[100][100];
    static int h, w, a, b;

    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        a = scanner.nextInt();
        b = scanner.nextInt();
        h = 2 * a + b - 1;
        w = 2 * b + a - 1;
        for (int i = 0; i < h; i++) {
            for (int j = 0; j < w; j++) {
                grid[i][j] = 0;
                visited[i][j] = 0;
            }
        }
        for (int i = 0; i < a; i++) {
            dfs(0, 2 * i + 1);
        }
        for (int i = 0; i < b; i++) {
            dfs(2 * i + 1, w - 1);
        }
        System.out.println(h + " " + w);
        for (int i = 0; i < h; i++) {
            for (int j = 0; j < w; j++) {
                if (grid[i][j] == 1) {
                    System.out.print(".");
                } else {
                    System.out.print("#");
                }
            }
            System.out.println();
        }
    }

    static void dfs(int x, int y) {
        if (x < 0 || x >= h || y < 0 || y >= w || visited[x][y] == 1 || grid[x][y] == 1) {
            return;
        }
        visited[x][y] = 1;
        grid[x][y] = 1;
        dfs(x - 1, y);
        dfs(x + 1, y);
        dfs(x, y - 1);
        dfs(x, y + 1);
    }
}
