from collections import deque

def paint_grid(A, B):
    h = A + B + 2
    w = 2 * max(A, B) + 1
    grid = [['.' for _ in range(w)] for _ in range(h)]

    for i in range(1, h-1):
        for j in range(1, w-1):
            if i == 1 and j == 1:
                grid[i][j] = '#'
            elif i == 1 and j == w-2:
                grid[i][j] = '#'
            elif i == h-2 and j == 1:
                grid[i][j] = '#'
            elif i == h-2 and j == w-2:
                grid[i][j] = '#'
            elif i == 1 or i == h-2 or j == 1 or j == w-2:
                grid[i][j] = '#'
            elif (i-1) // 2 == (j-1) // 2:
                grid[i][j] = '#'
            elif (i-1) // 2 == (j-1) // 3:
                grid[i][j] = '#'
            elif (i-1) // 3 == (j-1) // 2:
                grid[i][j] = '#'
            elif (i-1) // 3 == (j-1) // 3:
                grid[i][j] = '#'
            else:
                grid[i][j] = '.'

    for i in range(1, h-1):
        for j in range(1, w-1):
            if grid[i][j] == '.':
                if i == 1 and j == 2:
                    grid[i][j] = '.'
                elif i == 1 and j == w-1:
                    grid[i][j] = '.'
                elif i == h-2 and j == 2:
                    grid[i][j] = '.'
                elif i == h-2 and j == w-1:
                    grid[i][j] = '.'
                elif i == 1 or i == h-2 or j == 1 or j == w-2:
                    grid[i][j] = '.'
                elif (i-1) // 2 == (j-1) // 2:
                    grid[i][j] = '.'
                elif (i-1) // 2 == (j-1) // 3:
                    grid[i][j] = '.'
                elif (i-1) // 3 == (j-1) // 2:
                    grid[i][j] = '.'
                elif (i-1) // 3 == (j-1) // 3:
                    grid[i][j] = '.'
                else:
                    grid[i][j] = '.'

    for i in range(1, h-1):
        for j in range(1, w-1):
            if grid[i][j] == '.':
                grid[i][j] = '.'

    return grid

def print_grid(grid):
    h = len(grid)
    w = len(grid[0])
    print(f'{h} {w}')
    for row in grid:
        print(''.join(row))

A, B = map(int, input().split())
grid = paint_grid(A, B)
print_grid(grid)
