#include <iostream>
#include <vector>
#include <algorithm>

int main() {
    int N, t;
    std::cin >> N >> t;

    std::vector<std::pair<int, int>> buildings(N);
    for (int i = 0; i < N; ++i) {
        std::cin >> buildings[i].first >> buildings[i].second;
    }

    std::sort(buildings.begin(), buildings.end());

    int max_height = 0;
    for (int i = 0; i < N; ++i) {
        if (buildings[i].first < t) {
            max_height = std::max(max_height, buildings[i].second);
        }
    }

    double required_height = static_cast<double>(max_height) * (t - 1) / (buildings[N - 1].first - 1);
    if (buildings[N - 1].first == 1) {
        required_height = max_height;
    }

    std::cout << required_height << std::endl;

    return 0;
}
