import sys
input = sys.stdin.readline

mod = 10**9 + 7

def ksm(a, b):
    res = 1
    while b:
        if b & 1:
            res = res * a % mod
        a = a * a % mod
        b >>= 1
    return res

def find(x):
    if fa[x] == x:
        return x
    fa[x] = find(fa[x])
    return fa[x]

def merge(u, v, w):
    u = find(u)
    v = find(v)
    if u == v:
        return
    if sz[u] < sz[v]:
        u, v = v, u
    sz[u] += sz[v]
    fa[v] = u
    e[u].append((v, w))

def dfs(u, f, w):
    for v, e_w in e[u]:
        if v == f:
            continue
        dfs(v, u, e_w)
        dp[u][v] = (dp[v][u] + 1) % mod
        dp[u][v] = (dp[u][v] + dp[u][f]) % mod
    dp[u][f] = 1

def solve():
    n, m = map(int, input().split())
    x = int(input())
    fa = [i for i in range(n + 1)]
    sz = [1] * (n + 1)
    e = [[] for _ in range(n + 1)]
    for _ in range(m):
        u, v, w = map(int, input().split())
        merge(u, v, w)
    dp = [[0] * (n + 1) for _ in range(n + 1)]
    dfs(1, 0, 0)
    ans = 0
    for i in range(1, n + 1):
        for j in range(1, n + 1):
            if i == j:
                continue
            if find(i) == find(j):
                continue
            if dp[i][j] == 0:
                continue
            if i == 1 and j == n:
                if w[i][j] == x:
                    ans = (ans + 1) % mod
                continue
            if w[i][j] == x:
                ans = (ans + dp[i][j]) % mod
            else:
                ans = (ans + dp[i][j] * ksm(2, w[i][j] - x) % mod) % mod
    print(ans)

T = 1
for _ in range(T):
    solve()
