#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MOD 1000000007

long long power(long long a, long long b) {
    long long res = 1;
    while (b) {
        if (b & 1) res = res * a % MOD;
        a = a * a % MOD;
        b >>= 1;
    }
    return res;
}

int main() {
    int n, m;
    scanf("%d %d", &n, &m);
    int a[m + 1];
    for (int i = 1; i <= m; i++) {
        scanf("%d", &a[i]);
    }
    int max = 1 << n;
    long long ans = 1;
    for (int i = 1; i <= m; i++) {
        int x = a[i];
        int cnt = 0;
        for (int j = 1; j <= max; j++) {
            if (j != 1 && (j & x) == 0) cnt++;
        }
        ans = ans * power(2, cnt) % MOD;
    }
    for (int i = 1; i <= n; i++) {
        ans = ans * power(2, (1 << i) - 1) % MOD;
    }
    printf("%lld\n", ans);
    return 0;
}
