import java.io.*;
import java.util.*;

public class Main {
    static final int MOD = 1000000007;
    static int n, m, ans;
    static int[] a = new int[20];
    static int[] pow2 = new int[20];

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        StringTokenizer st = new StringTokenizer(br.readLine());

        n = Integer.parseInt(st.nextToken());
        m = Integer.parseInt(st.nextToken());
        pow2[0] = 1;
        for (int i = 1; i <= n; i++) {
            pow2[i] = (int) ((long) pow2[i - 1] * 2 % MOD);
        }

        st = new StringTokenizer(br.readLine());
        for (int i = 1; i <= m; i++) {
            a[i] = Integer.parseInt(st.nextToken());
        }

        for (int i = 0; i < (1 << n); i++) {
            if (f(i)) {
                ans = (int) ((ans + (long) pow2[n - 1] * (i & 1)) % MOD);
            }
        }

        pw.println(ans);
        pw.close();
    }

    static boolean f(int x) {
        for (int i = 1; i <= m; i++) {
            if (a[i] == x + 1) return false;
        }
        for (int i = 1; i <= n; i++) {
            if (((x >> (i - 1)) & 1) == 0) continue;
            if (((x >> (i - 1)) & 1) == 1) return false;
            for (int j = 1; j <= m; j++) {
                if (a[j] == (x + (1 << (i - 1))) + 1) return false;
            }
        }
        return true;
    }
}
