#include <stdio.h>
#include <stdlib.h>

typedef struct {
    int x, y;
} Point;

int compare(const void *a, const void *b) {
    Point *p1 = (Point *)a;
    Point *p2 = (Point *)b;
    if (p1->x < p2->x) return -1;
    if (p1->x > p2->x) return 1;
    if (p1->y < p2->y) return -1;
    if (p1->y > p2->y) return 1;
    return 0;
}

int main() {
    int n;
    scanf("%d", &n);
    Point red[n], blue[n];
    for (int i = 0; i < n; i++) {
        scanf("%d %d", &red[i].x, &red[i].y);
    }
    for (int i = 0; i < n; i++) {
        scanf("%d %d", &blue[i].x, &blue[i].y);
    }
    qsort(red, n, sizeof(Point), compare);
    qsort(blue, n, sizeof(Point), compare);
    int count = 0;
    int i = 0, j = 0;
    while (i < n && j < n) {
        if (red[i].x < blue[j].x && red[i].y < blue[j].y) {
            count++;
            i++;
            j++;
        } else if (red[i].x < blue[j].x) {
            i++;
        } else {
            j++;
        }
    }
    printf("%d\n", count);
    return 0;
}
