#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
    int n;
    cin >> n;

    vector<pair<int, int>> red(n), blue(n);
    for (int i = 0; i < n; i++) {
        cin >> red[i].first >> red[i].second;
    }
    for (int i = 0; i < n; i++) {
        cin >> blue[i].first >> blue[i].second;
    }

    sort(red.begin(), red.end());
    sort(blue.begin(), blue.end());

    int count = 0;
    int i = 0, j = 0;
    while (i < n && j < n) {
        if (red[i].first < blue[j].first && red[i].second < blue[j].second) {
            count++;
            i++;
            j++;
        } else if (red[i].first < blue[j].first) {
            i++;
        } else {
            j++;
        }
    }

    cout << count << endl;

    return 0;
}
