import java.util.Arrays;
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        int[][] red = new int[n][2];
        int[][] blue = new int[n][2];

        for (int i = 0; i < n; i++) {
            red[i][0] = scanner.nextInt();
            red[i][1] = scanner.nextInt();
        }

        for (int i = 0; i < n; i++) {
            blue[i][0] = scanner.nextInt();
            blue[i][1] = scanner.nextInt();
        }

        Arrays.sort(red, (a, b) -> a[0] == b[0] ? a[1] - b[1] : a[0] - b[0]);
        Arrays.sort(blue, (a, b) -> a[0] == b[0] ? a[1] - b[1] : a[0] - b[0]);

        int count = 0;
        int i = 0, j = 0;
        while (i < n && j < n) {
            if (red[i][0] < blue[j][0] && red[i][1] < blue[j][1]) {
                count++;
                i++;
                j++;
            } else if (red[i][0] < blue[j][0]) {
                i++;
            } else {
                j++;
            }
        }

        System.out.println(count);
    }
}
