#include <stdio.h>

int main() {
    int n;
    scanf("%d", &n);

    int a[n], b[n];
    for (int i = 0; i < n; i++) {
        scanf("%d", &a[i]);
    }
    for (int i = 0; i < n; i++) {
        scanf("%d", &b[i]);
    }

    int xor = 0;
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            xor ^= (a[i] + b[j]);
        }
    }

    printf("%d\n", xor);

    return 0;
}
