#include <stdio.h>
#include <stdlib.h>

int is_leap_year(int year) {
    if (year % 400 == 0) return 1;
    if (year % 100 == 0) return 0;
    if (year % 4 == 0) return 1;
    return 0;
}

int max_days(int month) {
    if (month == 2) {
        if (is_leap_year(1)) return 29;
        else return 28;
    }
    if (month == 4 || month == 6 || month == 9 || month == 11) return 30;
    return 31;
}

int max_age_diff(int y1, int m1, int d1, int y2, int m2, int d2) {
    int max_diff = 0;
    int diff = 0;
    int max_year = 3000;
    int max_month = 12;
    int max_day = max_days(max_month);

    for (int year = y1; year <= max_year; year++) {
