#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAXN 5000
#define INF 1000000000

typedef struct {
    int h, p;
} Participant;

int compare(const void *a, const void *b) {
    Participant *p1 = (Participant *)a;
    Participant *p2 = (Participant *)b;
    return p2->h - p1->h;
}

int main() {
    int n;
    scanf("%d", &n);
    Participant participants[MAXN];
    for (int i = 0; i < n; i++) {
        scanf("%d %d", &participants[i].h, &participants[i].p);
    }
    qsort(participants, n, sizeof(Participant), compare);
    int max = 0;
    int sum = 0;
    for (int i = 0; i < n; i++) {
        if (sum + participants[i].p <= participants[i].h) {
            sum += participants[i].p;
            max++;
        }
    }
    printf("%d\n", max);
    return 0;
}
