import java.util.Arrays;
import java.util.Comparator;
import java.util.Scanner;

public class Main {
    static class Participant {
        long height;
        long power;

        public Participant(long height, long power) {
            this.height = height;
            this.power = power;
        }
    }

    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        Participant[] participants = new Participant[n];

        for (int i = 0; i < n; i++) {
            long height = scanner.nextLong();
            long power = scanner.nextLong();
            participants[i] = new Participant(height, power);
        }

        Arrays.sort(participants, Comparator.comparingLong(p -> p.height));

        long[] dp = new long[n + 1];
        Arrays.fill(dp, 0);

        for (int i = 0; i < n; i++) {
            for (int j = n - 1; j >= 0; j--) {
                if (participants[j].height >= dp[i]) {
                    dp[i + 1] = Math.max(dp[i + 1], dp[i] + participants[j].power);
                }
            }
        }

        System.out.println(Arrays.stream(dp).max().getAsLong());
    }
}
