#include <iostream>
#include <string>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
    string s;
    cin >> s;
    int n;
    cin >> n;
    vector<pair<int, int>> ops(n);
    for (int i = 0; i < n; i++) {
        cin >> ops[i].first >> ops[i].second;
    }

    vector<int> maxR(n);
    for (int i = n - 1; i >= 0; i--) {
        maxR[i] = max(maxR[i + 1], ops[i].second);
    }

    int l = 0, r = 0;
    for (int i = 0; i < n; i++) {
        if (ops[i].first - 1 <= r) {
            if (ops[i].second > r) {
                r = ops[i].second;
            }
            continue;
        }
        int maxRVal = maxR[i];
        if (maxRVal < ops[i].first - 1) {
            cout << "NO" << endl;
            return 0;
        }
        if (maxRVal > ops[i].second) {
            maxRVal = ops[i].second;
        }
        for (int j = ops[i].first - 1; j <= maxRVal; j++) {
            if (s[j] != s[ops[i].second - (j - ops[i].first + 1)]) {
                cout << "NO" << endl;
                return 0;
            }
        }
        r = maxRVal;
    }
    cout << "YES" << endl;
    return 0;
}
