import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        String s = scanner.next();
        int n = scanner.nextInt();
        int[][] operations = new int[n][2];

        for (int i = 0; i < n; i++) {
            operations[i][0] = scanner.nextInt();
            operations[i][1] = scanner.nextInt();
        }

        int[] max = new int[s.length()];
        int[] min = new int[s.length()];

        for (int i = 0; i < s.length(); i++) {
            max[i] = s.charAt(i) - 'a';
            min[i] = s.charAt(i) - 'a';
        }

        for (int i = 0; i < n; i++) {
            int l = operations[i][0] - 1;
            int r = operations[i][1] - 1;

            for (int j = l; j <= r; j++) {
                max[j] = Math.max(max[j], (char) ((s.charAt(j) - 'a' + 1) % 26 + 'a') - 'a');
                min[j] = Math.min(min[j], (char) ((s.charAt(j) - 'a' - 1 + 26) % 26 + 'a') - 'a');
            }
        }

        boolean isPalindrome = true;
        for (int i = 0; i < s.length() / 2; i++) {
            if (max[i] < min[i]) {
                isPalindrome = false;
                break;
            }
        }

        System.out.println(isPalindrome ? "YES" : "NO");
    }
}
