import sys

s = input()
n = int(input())
ops = []
for _ in range(n):
    l, r = map(int, input().split())
    ops.append((l, r))

def check(s, ops):
    n = len(s)
    for l, r in ops:
        for i in range(l-1, r):
            if s[i] == 'z':
                s[i] = 'a'
            else:
                s[i] = chr(ord(s[i]) + 1)
    return s == s[::-1]

def solve(s, ops):
    n = len(s)
    for i in range(n // 2):
        if s[i] != s[n - i - 1]:
            l, r = i, n - i - 1
            for op in ops:
                if op[0] <= l and op[1] >= r:
                    l = max(l, op[0])
                    r = min(r, op[1])
            if l > r:
                return False
            for j in range(l, r + 1):
                if s[j] == 'z':
                    s[j] = 'a'
                else:
                    s[j] = chr(ord(s[j]) + 1)
    return True

print("YES" if solve(s, ops) or check(s, ops) else "NO")
