#include <stdio.h>

int main() {
    int N = 1000;
    int K = 1;

    printf("%d %d\n", N, K);

    for (int i = 1; i <= N; i++) {
        for (int j = 1; j <= K; j++) {
            int num = (i + j - 1) % N + 1;
            printf("%d ", num);
        }
        printf("\n");
    }

    return 0;
}
