int daysInMonth(int year, int month) {
    if (month == 2) {
        if ((year % 4 == 0 && year % 100 != 0) || year % 400 == 0)
            return 29;
        else
            return 28;
    } else if (month == 4 || month == 6 || month == 9 || month == 11)
        return 30;
    else
        return 31;
}

int maxAgeDiff(int y1, int m1, int d1, int y2, int m2, int d2) {
    int maxDiff = 0;
    int age1 = 0, age2 = 0;
    int year = y1;
    while (true) {
        int days = 0;
        for (int i = 1; i <= 12; i++) {
            if (i == m1 && d1 > days) {
                age1++;
                days = daysInMonth(year, i);
            } else if (i == m2 && d2 > days) {
                age2++;
                days = daysInMonth(year, i);
            } else {
                days += daysInMonth(year, i);
            }
        }
        if (age1 > age2) {
            maxDiff = age1 - age2;
            break;
        } else if (age2 > age1) {
            break;
        }
        year++;
    }
    return maxDiff;
}

