public class Main {
    public static void main(String[] args) {
        int N = 1000;
        int K = 1;

        System.out.println(N + " " + K);

        for (int i = 1; i <= N; i++) {
            StringBuilder sb = new StringBuilder();
            for (int j = 1; j <= K; j++) {
                int num = (i + j - 1) % N + 1;
                sb.append(num).append(" ");
            }
            System.out.println(sb.toString().trim());
        }
    }
}
