#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

const int MOD = 1e9 + 7;

long long power(long long a, long long b) {
    long long res = 1;
    while (b) {
        if (b & 1) res = res * a % MOD;
        a = a * a % MOD;
        b >>= 1;
    }
    return res;
}

int main() {
    int n, k;
    cin >> n >> k;

    vector<vector<long long>> dp(n + 1, vector<long long>(k + 1));
    for (int i = 1; i <= k; i++) {
        dp[i][i] = i;
    }

    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= k; j++) {
            for (int x = 1; x <= j; x++) {
                dp[i][j] = (dp[i][j] + dp[i - 1][j - x] * x % MOD) % MOD;
            }
        }
    }

    long long ans = 0;
    for (int i = 0; i <= k; i++) {
        ans = (ans + dp[n][i]) % MOD;
    }

    cout << ans << endl;

    return 0;
}
