#include <stdio.h>
#include <stdlib.h>

#define MAX_H 40
#define MAX_W 40

int h, w;
char grid[MAX_H + 1][MAX_W + 1];
int dp[MAX_H + 1][MAX_W + 1][2];

int dfs(int i, int j, int p) {
    if (i < 0 || i >= h || j < 0 || j >= w) return 0;
    if (dp[i][j][p] != -1) return dp[i][j][p];
    int res = 0;
    if (grid[i][j] == '+') {
        if (p == 0) {
            res = 1 + dfs(i - 1, j, 0) + dfs(i + 1, j, 0);
        } else {
            res = 1 + dfs(i - 1, j, 1) + dfs(i + 1, j, 1);
        }
    } else if (grid[i][j] == '#') {
        res = dfs(i - 1, j, 1) + dfs(i + 1, j, 1);
    } else {
        res = dfs(i - 1, j, 1) + dfs(i + 1, j, 1);
    }
    dp[i][j][p] = res;
    return res;
}

int main() {
    int i, j;
    scanf("%d %d", &h, &w);
    for (i = 0; i < h; i++) {
        scanf("%s", grid[i]);
    }
    int penguin_i, penguin_j;
    for (i = 0; i < h; i++) {
        for (j = 0; j < w; j++) {
            if (grid[i][j] == 'P') {
                penguin_i = i;
                penguin_j = j;
                break;
            }
        }
    }
    for (i = 0; i <= h; i++) {
        for (j = 0; j <= w; j++) {
            for (int p = 0; p < 2; p++) {
                dp[i][j][p] = -1;
            }
        }
    }
    printf("%d\n", dfs(penguin_i, penguin_j, 1) - 1);
    return 0;
}
