#include <iostream>
#include <vector>
#include <queue>
#include <string>
#include <algorithm>

using namespace std;

const int MAX_H = 40;
const int MAX_W = 40;

int h, w;
char grid[MAX_H + 1][MAX_W + 1];
int dist[MAX_H + 1][MAX_W + 1];
int iceberg[MAX_H + 1][MAX_W + 1];

int dx[] = { 0, 0, 1, -1 };
int dy[] = { 1, -1, 0, 0 };

int bfs(int x, int y) {
    queue<pair<int, int>> q;
    q.push({ x, y });
    dist[x][y] = 0;
    iceberg[x][y] = 0;

    while (!q.empty()) {
        int cx = q.front().first;
        int cy = q.front().second;
        q.pop();

        for (int i = 0; i < 4; i++) {
            int nx = cx + dx[i];
            int ny = cy + dy[i];

            if (nx < 0 || ny < 0 || nx >= h || ny >= w) continue;
            if (dist[nx][ny] != -1) continue;
            if (grid[nx][ny] == '#') continue;

            dist[nx][ny] = dist[cx][cy] + 1;
            q.push({ nx, ny });
        }
    }

    int count = 0;
    for (int i = 0; i < h; i++) {
        for (int j = 0; j < w; j++) {
            if (dist[i][j] == -1) continue;
            if (grid[i][j] == '#') continue;
            if (dist[i][j] % 2 == 0) continue;

            count++;
        }
    }

    return count;
}

int main() {
    cin >> h >> w;
    for (int i = 0; i < h; i++) {
        for (int j = 0; j < w; j++) {
            cin >> grid[i][j];
        }
    }

    int penguin_x = -1, penguin_y = -1;
    for (int i = 0; i < h; i++) {
        for (int j = 0; j < w; j++) {
            if (grid[i][j] == 'P') {
                penguin_x = i;
                penguin_y = j;
                break;
            }
        }
    }

    for (int i = 0; i < h; i++) {
        for (int j = 0; j < w; j++) {
            dist[i][j] = -1;
        }
    }

    int ans = bfs(penguin_x, penguin_y);
    cout << ans << endl;

    return 0;
}
