import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);

        int[] hatsumi = new int[3];
        int[] taku = new int[3];

        for (int i = 0; i < 2; i++) {
            String[] input = scanner.nextLine().split(" ");
            hatsumi[i] = Integer.parseInt(input[0]);
            taku[i] = Integer.parseInt(input[0]);
            hatsumi[i + 1] = Integer.parseInt(input[1]);
            taku[i + 1] = Integer.parseInt(input[1]);
            hatsumi[i + 2] = Integer.parseInt(input[2]);
            taku[i + 2] = Integer.parseInt(input[2]);
        }

        int maxDiff = 0;

        for (int year = Math.max(hatsumi[0], taku[0]); year <= 3000; year++) {
            boolean isLeapYear = (year % 4 == 0 && year % 100 != 0) || (year % 400 == 0);
            int hatsumiAge = getAge(hatsumi[0], hatsumi[1], hatsumi[2], year, isLeapYear);
            int takuAge = getAge(taku[0], taku[1], taku[2], year, isLeapYear);
            maxDiff = Math.max(maxDiff, Math.abs(hatsumiAge - takuAge));
        }

        System.out.println(maxDiff);
    }

    public static int getAge(int birthYear, int birthMonth, int birthDay, int currentYear, boolean isLeapYear) {
        int age = currentYear - birthYear;
        if (currentYear == birthYear) {
            if (birthMonth > currentMonth || (birthMonth == currentMonth && birthDay > currentDay)) {
                age--;
            }
        } else if (currentYear == birthYear + 1) {
            if (isLeapYear && birthMonth == 2 && birthDay == 29) {
                age--;
            }
        }
        return age;
    }

    public static int currentMonth = 12;
    public static int currentDay = 31;
}
