from collections import deque

def bfs(i, j):
    q = deque([(i, j)])
    visited = {(i, j)}
    while q:
        x, y = q.popleft()
        for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
            nx, ny = x + dx, y + dy
            if 0 <= nx < h and 0 <= ny < w and grid[nx][ny] == '+' and (nx, ny) not in visited:
                q.append((nx, ny))
                visited.add((nx, ny))
    return visited

def solve():
    global h, w, grid
    h, w = map(int, input().split())
    grid = [list(input()) for _ in range(h)]
    p_i, p_j = -1, -1
    for i in range(h):
        for j in range(w):
            if grid[i][j] == 'P':
                p_i, p_j = i, j
                break
        if p_i != -1:
            break
    iceberg = bfs(p_i, p_j)
    iceberg.remove((p_i, p_j))
    ans = len(iceberg)
    for i in range(h):
        for j in range(w):
            if grid[i][j] == '#' and (i, j) not in iceberg:
                grid[i][j] = '+'
                iceberg = bfs(i, j)
                iceberg.remove((i, j))
                ans = min(ans, len(iceberg))
    print(ans)

solve()
