#include <stdio.h>
#include <stdlib.h>

#define N 18
#define MAX 1 << N

int parent[MAX];
int size[MAX];

void make_set(int i) {
    parent[i] = i;
    size[i] = 1;
}

int find_set(int i) {
    if (parent[i] != i) {
        parent[i] = find_set(parent[i]);
    }
    return parent[i];
}

void union_sets(int a, int b) {
    a = find_set(a);
    b = find_set(b);
    if (a != b) {
        if (size[a] < size[b]) {
            int temp = a;
            a = b;
            b = temp;
        }
        parent[b] = a;
        size[a] += size[b];
    }
}

int main() {
    int n;
    scanf("%d", &n);
    int a[MAX];
    for (int i = 0; i < MAX; i++) {
        scanf("%d", &a[i]);
    }
    for (int i = 0; i < MAX; i++) {
        make_set(i);
    }
    for (int i = 0; i < MAX; i++) {
        if (a[i] != 0) {
            union_sets(a[i] - 1, i);
        }
    }
    int root = find_set(0);
    int count = 0;
    for (int i = 0; i < MAX; i++) {
        if (find_set(i) == root) {
            count++;
        }
    }
    if (count != 1) {
        printf("NO\n");
        return 0;
    }
    printf("YES\n");
    for (int i = 0; i < MAX; i++) {
        if (find_set(i) == root) {
            printf("%d ", i + 1);
        }
    }
    printf("\n");
    return 0;
}
