import java.io.*;
import java.util.*;

public class Main {
    static int N;
    static int[] A;
    static int[] parent;
    static int[] size;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        StringTokenizer st = new StringTokenizer(br.readLine());

        N = Integer.parseInt(st.nextToken());
        int n = 1 << N;
        A = new int[n];
        st = new StringTokenizer(br.readLine());
        for (int i = 0; i < n; i++) {
            A[i] = Integer.parseInt(st.nextToken());
        }

        parent = new int[n];
        size = new int[n];
        for (int i = 0; i < n; i++) {
            parent[i] = i;
            size[i] = 1;
        }

        for (int i = 0; i < n; i++) {
            if (A[i] == 0) continue;
            int j = i;
            while (j < n - 1 && A[j] == 0) {
                j++;
            }
            if (j == n - 1) break;
            int x = find(A[i]);
            int y = find(A[j]);
            if (x != y) {
                union(x, y);
            } else {
                pw.println("NO");
                pw.close();
                return;
            }
        }

        List<Integer> ans = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            if (A[i] != 0) ans.add(A[i]);
        }

        int[] order = new int[n];
        for (int i = 0; i < n; i++) {
            order[i] = i;
        }

        Arrays.sort(order, (x, y) -> {
            int a = find(A[x]);
            int b = find(A[y]);
            if (a == b) {
                return Integer.compare(size[x], size[y]);
            } else {
                return Integer.compare(a, b);
            }
        });

        for (int i = 0; i < n; i++) {
            if (A[order[i]] != 0) {
                ans.add(A[order[i]]);
            }
        }

        pw.println("YES");
        for (int i = 0; i < n; i++) {
            pw.print(ans.get(i) + " ");
        }
        pw.println();
        pw.close();
    }

    static int find(int x) {
        if (parent[x] == x) return x;
        return parent[x] = find(parent[x]);
    }

    static void union(int x, int y) {
        x = find(x);
        y = find(y);
        if (x != y) {
            if (size[x] < size[y]) {
                int temp = x;
                x = y;
                y = temp;
            }
            parent[y] = x;
            size[x] += size[y];
        }
    }
}
