import sys
input = sys.stdin.readline

N = 18
MAX = 1 << N

def dfs(u, v):
    if u == v:
        return True
    if u > v:
        return False
    if not vis[u]:
        vis[u] = True
        for i in range(1, 1 << (n - 1)):
            if not vis[2 * u + i] and dfs(2 * u + i, v):
                return True
        return False
    return dfs(2 * u, v)

def solve():
    global n
    n = int(input())
    A = list(map(int, input().split()))
    vis = [False] * MAX
    for i in range(1 << n):
        if A[i - 1] != 0:
            vis[A[i - 1] - 1] = True
    for i in range(1 << n):
        if not vis[i]:
            print("NO")
            return
    print("YES")
    for i in range(1 << n):
        if vis[i]:
            print(A[i - 1] if A[i - 1] != 0 else 0, end=' ')
    print()

T = 1
for _ in range(T):
    solve()
