#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <limits.h>

#define N 200000
#define INF 0x3f3f3f3f

typedef struct Edge {
    int u, v, w;
} Edge;

typedef struct Node {
    int x, id;
} Node;

int cmp(const void *a, const void *b) {
    Node *x = (Node *)a;
    Node *y = (Node *)b;
    return x->x - y->x;
}

int find(int *p, int u) {
    if (p[u] != u) p[u] = find(p, p[u]);
    return p[u];
}

int main() {
    int n;
    scanf("%d", &n);
    Node nodes[N + 1];
    for (int i = 1; i <= n; i++) {
        scanf("%d", &nodes[i].x);
        nodes[i].id = i;
    }
    Edge edges[N];
    for (int i = 1; i < n; i++) {
        int u, v, w;
        scanf("%d%d%d", &u, &v, &w);
        edges[i].u = u;
        edges[i].v = v;
        edges[i].w = w;
    }
    qsort(nodes + 1, n, sizeof(Node), cmp);
    qsort(edges + 1, n - 1, sizeof(Edge), cmp);
    int p[N + 1];
    for (int i = 1; i <= n; i++) p[i] = i;
    long long ans = 0;
    for (int i = 1; i < n; i++) {
        int u = find(p, edges[i].u);
        int v = find(p, edges[i].v);
        if (u != v) {
            ans += edges[i].w + nodes[u].x + nodes[v].x;
            p[v] = u;
        }
    }
    for (int i = 1; i <= n; i++) {
        int u = find(p, i);
        for (int j = i + 1; j <= n; j++) {
            int v = find(p, j);
            if (u != v) {
                ans += nodes[u].x + nodes[v].x;
            }
        }
    }
    printf("%lld\n", ans);
    return 0;
}
