#include <iostream>
#include <vector>
#include <algorithm>
#include <queue>
#include <limits>

using namespace std;

struct Edge {
    int u, v, w;
    bool operator<(const Edge& other) const {
        return w > other.w;
    }
};

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int n;
    cin >> n;

    vector<int> x(n);
    for (int i = 0; i < n; ++i) {
        cin >> x[i];
    }

    vector<Edge> edges(n - 1);
    for (int i = 0; i < n - 1; ++i) {
        int u, v;
        long long c;
        cin >> u >> v >> c;
        --u; --v;
        edges[i] = {u, v, x[u] + x[v] + c};
    }

    sort(edges.begin(), edges.end());

    long long ans = 0;
    vector<bool> used(n);
    priority_queue<long long, vector<long long>, greater<long long>> pq;
    for (int i = 0; i < n - 1; ++i) {
        int u = edges[i].u, v = edges[i].v;
        if (!used[u] || !used[v]) {
            ans += edges[i].w;
            pq.push(edges[i].w);
            used[u] = used[v] = true;
        }
    }

    while (!pq.empty()) {
        long long w = pq.top();
        pq.pop();
        if (w != ans) {
            ans = numeric_limits<long long>::max();
            for (int i = 0; i < n - 1; ++i) {
                int u = edges[i].u, v = edges[i].v;
                if (!used[u] || !used[v]) {
                    ans = min(ans, edges[i].w);
                }
            }
        }
    }

    cout << ans << '\n';

    return 0;
}
