import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        String input = scanner.next();

        for (int i = 0; i < 4; i++) {
            for (int j = 0; j < 4; j++) {
                for (int k = 0; k < 4; k++) {
                    for (int l = 0; l < 4; l++) {
                        if (check(input.charAt(0) - '0', input.charAt(1) - '0', input.charAt(2) - '0', input.charAt(3) - '0',
                                (i == 0 ? '+' : '-'), (j == 0 ? '+' : '-'), (k == 0 ? '+' : '-'), (l == 0 ? '+' : '-'))) {
                            System.out.println(input.charAt(0) + "" + input.charAt(1) + "" + input.charAt(2) + "" + input.charAt(3)
                                    + "" + (i == 0 ? '+' : '-') + "" + (j == 0 ? '+' : '-') + "" + (k == 0 ? '+' : '-') + "" + (l == 0 ? '+' : '-') + "=7");
                            return;
                        }
                    }
                }
            }
        }
    }

    public static boolean check(int a, int b, int c, int d, char op1, char op2, char op3, char op4) {
        return (a + b + c + d == 7) && (eval(a, b, op1) + eval(c, d, op3) == eval(b, c, op2) + eval(d, a, op4));
    }

    public static int eval(int a, int b, char op) {
        return op == '+' ? a + b : a - b;
    }
}
