#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAX_H 200
#define MAX_W 200
#define MAX_C 10

int min(int a, int b) {
    return a < b ? a : b;
}

int main() {
    int H, W;
    scanf("%d %d", &H, &W);

    int c[MAX_C][MAX_C];
    for (int i = 0; i < MAX_C; i++) {
        for (int j = 0; j < MAX_C; j++) {
            c[i][j] = j == i ? 0 : 999;
        }
    }

    for (int i = 0; i < MAX_C; i++) {
        for (int j = 0; j < MAX_C; j++) {
            scanf("%d", &c[i][j]);
        }
    }

    int dp[MAX_H][MAX_W][MAX_C];
    for (int i = 0; i < H; i++) {
        for (int j = 0; j < W; j++) {
            for (int k = 0; k < MAX_C; k++) {
                dp[i][j][k] = 999;
            }
        }
    }

    for (int i = 0; i < H; i++) {
        for (int j = 0; j < W; j++) {
            int a;
            scanf("%d", &a);
            if (a == -1) {
                continue;
            }
            for (int k = 0; k < MAX_C; k++) {
                dp[i][j][k] = min(dp[i][j][k], c[a][k] + dp[i][j][k]);
            }
        }
    }

    int ans = 0;
    for (int i = 0; i < H; i++) {
        for (int j = 0; j < W; j++) {
            int a;
            scanf("%d", &a);
            if (a == -1) {
                continue;
            }
            ans += dp[i][j][1];
        }
    }

    printf("%d\n", ans);

    return 0;
}
