#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

const int INF = 1e9;

int main() {
    int H, W;
    cin >> H >> W;

    vector<vector<int>> c(10, vector<int>(10));
    for (int i = 0; i < 10; i++) {
        for (int j = 0; j < 10; j++) {
            cin >> c[i][j];
        }
    }

    vector<vector<int>> dp(H, vector<int>(10, INF));
    for (int i = 0; i < 10; i++) {
        dp[0][i] = 0;
    }

    for (int h = 0; h < H; h++) {
        vector<int> a(W);
        for (int w = 0; w < W; w++) {
            cin >> a[w];
        }

        for (int w = 0; w < W; w++) {
            if (a[w] != -1) {
                for (int i = 0; i < 10; i++) {
                    for (int j = 0; j < 10; j++) {
                        dp[h][j] = min(dp[h][j], dp[h][i] + c[i][j]);
                    }
                }
                dp[h][a[w]] = min(dp[h][a[w]], dp[h][a[w]] + c[a[w]][1]);
            }
        }
    }

    int ans = INF;
    for (int i = 0; i < 10; i++) {
        ans = min(ans, dp[H - 1][i]);
    }

    cout << ans << endl;

    return 0;
}
