import java.io.*;
import java.util.*;

public class Main {
    static final int INF = (int) 1e9;
    static int H, W;
    static int[][] c;
    static int[][] dp;
    static int[][] a;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        StringTokenizer st = new StringTokenizer(br.readLine());
        H = Integer.parseInt(st.nextToken());
        W = Integer.parseInt(st.nextToken());
        c = new int[10][10];
        dp = new int[10][10];
        a = new int[H + 1][W + 1];
        for (int i = 0; i < 10; i++) {
            Arrays.fill(dp[i], INF);
        }
        for (int i = 0; i < 10; i++) {
            st = new StringTokenizer(br.readLine());
            for (int j = 0; j < 10; j++) {
                c[i][j] = Integer.parseInt(st.nextToken());
            }
        }
        for (int i = 1; i <= H; i++) {
            st = new StringTokenizer(br.readLine());
            for (int j = 1; j <= W; j++) {
                a[i][j] = Integer.parseInt(st.nextToken());
            }
        }
        for (int i = 0; i < 10; i++) {
            dp[i][i] = 0;
        }
        for (int i = 1; i <= H; i++) {
            for (int j = 1; j <= W; j++) {
                if (a[i][j] != -1) {
                    for (int k = 0; k < 10; k++) {
                        dp[a[i][j]][1] = Math.min(dp[a[i][j]][1], dp[k][1] + c[k][a[i][j]]);
                    }
                }
            }
            for (int j = 1; j <= W; j++) {
                for (int k = 0; k < 10; k++) {
                    dp[k][j + 1] = Math.min(dp[k][j + 1], dp[k][j] + c[k][1]);
                }
            }
        }
        int ans = INF;
        for (int i = 0; i < 10; i++) {
            ans = Math.min(ans, dp[i][W]);
        }
        System.out.println(ans);
    }
}
