#include <iostream>
#include <string>
#include <algorithm>

int main() {
    std::string x, y;
    std::cin >> x >> y;

    int valX = std::stoi(x, 0, 16);
    int valY = std::stoi(y, 0, 16);

    if (valX < valY) {
        std::cout << "<";
    } else if (valX > valY) {
        std::cout << ">";
    } else {
        std::cout << "=";
    }

    return 0;
}
