import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        int m = scanner.nextInt();
        long total = 0;
        for (int i = 1; i <= m; i++) {
            total += 1900 * i * (long) Math.pow(0.5, i);
        }
        total += 100 * (n - m) * (long) Math.pow(0.5, m);
        System.out.println((long) Math.round(total * 2));
    }
}
