import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        int[] t = new int[n];
        for (int i = 0; i < n; i++) {
            t[i] = scanner.nextInt();
        }
        int lcm = t[0];
        for (int i = 1; i < n; i++) {
            lcm = lcm(t, lcm, t[i]);
        }
        int ans = 0;
        for (int i = 0; i < n; i++) {
            ans += (lcm / t[i]) - 1;
        }
        System.out.println(ans);
    }

    public static int lcm(int[] t, int a, int b) {
        return a * b / gcd(a, b);
    }

    public static int gcd(int a, int b) {
        if (b == 0) return a;
        return gcd(b, a % b);
    }
}
