import math

def max_earnings(N, a):
    max_divisors = [0] * (N + 1)
    for i in range(1, N + 1):
        for j in range(i, N + 1, i):
            max_divisors[j] = max(max_divisors[j], i)

    max_earnings = 0
    for i in range(1, N + 1):
        if a[i - 1] >= 0:
            max_earnings += a[i - 1]
        else:
            max_earnisors = 0
            for j in range(1, int(math.sqrt(abs(a[i - 1]))) + 1):
                if a[i - 1] % j == 0:
                    max_divisors[j] = max(max_divisors[j], max_divisors[i])
                    max_divisors[a[i - 1] // j] = max(max_divisors[a[i - 1] // j], max_divisors[i])
                    max_earnisors = max(max_earnisors, max_divisors[j])
                    max_earnisors = max(max_earnisors, max_divisors[a[i - 1] // j])
            max_earnings += max_earnisors * a[i - 1]

    return max_earnings

N = int(input())
a = list(map(int, input().split()))
print(max_earnings(N, a))
