#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define N 200000
#define Q 200000

int n, q;
int b[N + 1];
int l[Q + 1], r[Q + 1];
int cnt[N + 1];

int main() {
    scanf("%d", &n);
    for (int i = 1; i <= n; i++) {
        scanf("%d", &b[i]);
    }
    scanf("%d", &q);
    for (int i = 1; i <= q; i++) {
        scanf("%d %d", &l[i], &r[i]);
    }

    int ans = 0;
    for (int i = 1; i <= n; i++) {
        cnt[i] = cnt[i - 1] + (b[i] == 0);
    }

    for (int i = 1; i <= q; i++) {
        int x = lower_bound(l[i], r[i] + 1, cnt);
        ans += (n - x + 1) - (r[i] - l[i] + 1);
    }

    printf("%d\n", n - ans);

    return 0;
}

int lower_bound(int x, int y, int *cnt) {
    int l = 0, r = y;
    while (l < r) {
        int mid = (l + r) / 2;
        if (cnt[mid] < x) {
            l = mid + 1;
        } else {
            r = mid;
        }
    }
    return l;
}
