#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

struct Segment {
    int l, r;
};

bool cmp(const Segment& a, const Segment& b) {
    return a.l < b.l;
}

int main() {
    int n;
    cin >> n;
    vector<int> b(n);
    for (int i = 0; i < n; i++) {
        cin >> b[i];
    }
    int q;
    cin >> q;
    vector<Segment> segments(q);
    for (int i = 0; i < q; i++) {
        cin >> segments[i].l >> segments[i].r;
        segments[i].l--;
    }
    sort(segments.begin(), segments.end(), cmp);
    vector<int> pos(n, -1);
    int ans = 0;
    for (int i = 0; i < q; i++) {
        int l = segments[i].l, r = segments[i].r;
        while (pos[l] < r) {
            if (b[pos[l] + 1] == 0) {
                ans++;
            }
            pos[l]++;
        }
    }
    cout << n - ans << endl;
    return 0;
}
